// 歌曲详情

const createOption = require('../util/option.js')
module.exports = (query, request) => {
  query.ids = query.ids.split(/\s*,\s*/)
  const data = {
    c: '[' + query.ids.map((id) => '{"id":' + id + '}').join(',') + ']',
  }
  request(
    'POST',
    `/api/v3/song/detail`,
    data,
    createOption(query, 'weapi'),
  )
  const op = createOption(query, 'weapi')
  op.cookie = '_ga=GA1.1.2118325875.1688024288; Hm_lvt_6b8941338e0f095537972acf80ce49ce=1708331355,1708506234,1708687147; MUSIC_; NMTID=00Oj-FBvFn26RsxnUEijbFpVkCNRpoAAAGQZ7CRgg; _ga_KMJJCFZDKF=GS1.1.1719726543.256.0.1719726543.0.0.0'
  return request(
    'POST',
    `/api/v3/song/detail`,
    data,
    op,
  )
}

